// Extract OCR text from Google Vision response
const visionResponse = $input.first().json;

let ocrText = '';
try {
  ocrText = visionResponse.responses[0].fullTextAnnotation.text;
} catch (e) {
  throw new Error('Google Vision OCR failed to extract text from image. The image may be unclear or not contain readable text.');
}

if (!ocrText || ocrText.trim().length < 10) {
  throw new Error('OCR extracted very little text. Please try with a clearer image.');
}

return [{ json: { recipeText: ocrText, source: 'image' } }];

// Pass through text input directly
const body = $input.first().json.body;
const recipeText = body.text || '';

if (!recipeText || recipeText.trim().length < 10) {
  throw new Error('Recipe text is too short. Please provide more detail.');
}

return [{ json: { recipeText: recipeText, source: 'text' } }];

// Parse Claude's response and format for Google Sheets
const claudeResponse = $input.first().json;

let recipeJson;
try {
  // Extract text content from Claude API response
  const content = claudeResponse.content[0].text;

  // Try to parse JSON - handle potential markdown code fences
  let jsonStr = content.trim();
  if (jsonStr.startsWith('```')) {
    jsonStr = jsonStr.replace(/^```(?:json)?\n?/, '').replace(/\n?```$/, '');
  }

  recipeJson = JSON.parse(jsonStr);
} catch (e) {
  throw new Error(`Failed to parse Claude response as JSON: ${e.message}. Raw response: ${JSON.stringify(claudeResponse.content).substring(0, 500)}`);
}

// Validate required fields
const required = ['title', 'ingredients', 'instructions'];
for (const field of required) {
  if (!recipeJson[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Format for Google Sheets
const title = recipeJson.title || 'Untitled Recipe';
const description = recipeJson.description || '';

// Format ingredients as newline-separated list
const ingredients = Array.isArray(recipeJson.ingredients)
  ? recipeJson.ingredients.join('\n')
  : recipeJson.ingredients;

// Format instructions as newline-separated list
const instructions = Array.isArray(recipeJson.instructions)
  ? recipeJson.instructions.join('\n')
  : recipeJson.instructions;

const timestamp = new Date().toISOString();

return [{
  json: {
    title,
    description,
    ingredients,
    instructions,
    timestamp,
    status: 'New',
    // Pass title back for webhook response
    _responseTitle: title
  }
}];

